// Analytics module for LPA
// Dependencies: moj, jQuery

(function () {
  'use strict';

  if (typeof(gaConfig) === 'undefined') {
    moj.log('gaConfig not set. skipping Google Analytics tracking.');
    return;
  }

  moj.Modules.Analytics = {

    init: function () {
      this.bindEvents();

      // Check if we have permission to enable tracking
      if (typeof GOVUK.checkConsentCookieCategory === 'function'
          && GOVUK.checkConsentCookieCategory('analytics', 'usage')) {
          this.setup();
      }
    },

    bindEvents: function() {
      moj.Events.on('Analytics.start', this.setup)
    },

    setup: function() {
      GOVUK.Analytics.load();

      // Use document.domain in dev, preview and staging so that tracking works
      // Otherwise explicitly set the domain as lastingpowerofattorney.service.justice.gov.uk.
      var prodDomain = new RegExp('^(www\.)*lastingpowerofattorney\.service\.gov\.uk$')
      var cookieDomain = prodDomain.test(document.domain) ? '.lastingpowerofattorney.service.gov.uk' : document.domain;

      // Configure profiles and make interface public
      // for custom dimensions, virtual pageviews and events
      GOVUK.analytics = new GOVUK.Analytics({
        universalId: gaConfig.universalId  || '',
        cookieDomain: cookieDomain,
        allowLinker: true,
        allowAnchor: true,

        //TODO are we tracking this within lpa
        stripPostcodePII: true,
        stripDatePII: true
      });

      if (prodDomain.test(document.domain)) {
        GOVUK.analytics.addLinkedTrackerDomain(gaConfig.govId, 'govuk_shared', ['www.gov.uk', '.payments.service.gov.uk']);
      }

      // Track initial pageview
      if (typeof GOVUK.pageviewOptions !== 'undefined') {
        GOVUK.analytics.trackPageview(null, null, GOVUK.pageviewOptions);
      }
      else {
        GOVUK.analytics.trackPageview();
      }
    }
  };
})();;
// Analytics form error tracking module for LPA
// Dependencies: moj, jQuery

(function () {
  'use strict';

  moj.Modules.formErrorTracker = {

    init: function () {
      this.checkErrors();
      // Make available within popups by adding to the Events object
      moj.Events.on('formErrorTracker.checkErrors', this.checkErrors);
    },

    checkErrors: function(){
      // Iterating through link errors
      var errorsLinked = $('.error-summary-list li a')
      for (var i = 0; i < errorsLinked.length; i++) {
        moj.Modules.formErrorTracker.trackError(errorsLinked[i])
      }

      // Iterating through text errors
      var errorsText = $('.error-summary-text p')
      for (var i = 0; i < errorsText.length; i++) {
        moj.Modules.formErrorTracker.trackErrorText(errorsText[i])
      }
    },

    trackError: function(error) {
      var $error = $(error)
      var errorText = $.trim($error.text())
      var errorID = $error.attr('href')
      var questionText = this.getQuestionText(error)

      var actionLabel = errorID + ' - ' + errorText

      var options = {
        transport: 'beacon',
        label: actionLabel
      }

      GOVUK.analytics.trackEvent('form error', questionText, options)
    },

    trackErrorText: function(error){
      var trackingContext = $('.error-summary-text').data('tracking-context')
      var trackingSummary = $(error).data('tracking-summary')

      var options = {
        transport: 'beacon',
        label: trackingSummary
      }

      GOVUK.analytics.trackEvent('form error', trackingContext, options)
    },

    getQuestionText: function(error) {
      var $error = $(error)
      var errorID = $error.attr('href')
      var questionText

      if (errorID.indexOf('secret_') >= 0) {
        questionText = 'CSRF error'
      } else {
        var $element = $(errorID)
        var elementID = $element.prop('id')

        var nodeName = document.getElementById(elementID).nodeName.toLowerCase()
        var legendText

        // If the error is on an input or textarea
        if (nodeName === 'input' || nodeName === 'textarea' || nodeName === 'select') {
          // Get the label
          questionText = $.trim($('label[for="' + elementID + '"]')[0].childNodes[0].nodeValue)
          // Get the legend for that label/input
          legendText = $.trim($element.closest('fieldset').find('legend').text())
          // combine the legend with the label
          questionText = legendText.length > 0 ? legendText + ': ' + questionText : questionText
        }
        // If the error is on a fieldset (for radio buttons and checkboxes)
        else if (nodeName === 'fieldset') {
          legendText = $.trim($element.find('legend').text())
          questionText = legendText
        }
        // Anything else
        else {
          questionText = ''
        }
      }

      return questionText
    }
  };
})();;
(function() {
    'use strict';

    moj.Modules.CookieConsent = {
        init: function () {
            if (!this.isInCookiesPage() && !this.isInIframe() && 'true' !== window.GOVUK.cookie('seen_cookie_message')) {
                this.displayCookieMessage(true);
                window.GOVUK.cookie('cookie_policy') || window.GOVUK.setDefaultConsentCookie()
            }

            this.enableAllCookies = this.enableAllCookies.bind(this);
            var acceptButton = document.querySelector('.global-cookie-message__button_accept');
            acceptButton.addEventListener('click', this.enableAllCookies);
        },

        displayCookieMessage: function(show) {
            var message = document.getElementById('global-cookie-message');

            if (show) {
                message.style.display = 'block';
            } else {
                message.removeAttribute('style');
            }
        },

        enableAllCookies: function(evt) {
            window.GOVUK.approveAllCookieTypes();
            window.GOVUK.cookie('seen_cookie_message', 'true');
            this.displayCookieMessage(false);

            // enable analytics and fire off a pageview
            moj.Events.trigger('Analytics.start');
        },

        isInCookiesPage: function() {
            return '/cookies' === window.location.pathname
        },

        isInIframe: function () {
            return window.parent && window.location !== window.parent.location
        }
    }
})();;
